import axios from 'axios';

import { ACCOUNT_LOADING, CREATE_ACCOUNT_FAILURE, CREATE_ACCOUNT_SUCCESS } from './types';

// create account 
export const createAccount = (userData) => {
    return async dispatch => {
        dispatch(accountLoading())
        try {
            const baseUrl = process.env.REACT_APP_BASE_URL;
            const response = await axios.post(`${baseUrl}/v1/accounts`, { userData: userData }, {
                auth: {
                    username: process.env.REACT_APP_API_KEY,
                    password: process.env.REACT_APP_API_SECRET
                }
            })
            console.log(response.data)
            if (response.data) {
                dispatch({
                    type: CREATE_ACCOUNT_SUCCESS,
                    payload: response.data
                })
            }
        } catch (e) {
            dispatch({
                type: CREATE_ACCOUNT_FAILURE,
                payload: e
            })
        }
    }
}
//account loading 
export const accountLoading = () => {
    return {
        type: ACCOUNT_LOADING
    }
}
