import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { createAccount } from '../../actions/accountAction';


const TrustedContacts = ({ prevStep, values }) => {
    const dispatch = useDispatch();
    const accData = useSelector(state => state.acc);

    // handleBack
    const handleBack = (e) => {
        e.preventDefault()
        prevStep();
    }
    const { email_address,
        phone_number,
        street_address,
        city,
        state,
        postal_code,
        given_name,
        family_name,
        date_of_birth,
        tax_id,
        tax_id_type,
        country_of_citizenship,
        country_of_birth,
        country_of_tax_residency,
        visa_type,
        visa_expiration_date,
        date_of_departure_from_usa,
        permanent_resident,
        funding_source,
        annual_income_min,
        annual_income_max,
        liquid_net_worth_min,
        liquid_net_worth_max,
        total_net_worth_min,
        total_net_worth_max,
        is_control_person,
        is_affiliated_exchange_or_finra,
        is_politically_exposed,
        immediate_family_exposed,
        employment_status,
        employer_name,
        employer_address,
        employment_position,
        agreement,
        signed_at,
        ip_address,
        revision,
        documents
    } = values;
    //handle account 
    const handleAccount = (e) => {
        e.preventDefault();
        const userData = {
            "contact": {
                "email_address": "cool_karthik@exam.com",
                "phone_number": "555-666-7788",
                "street_address": ["20 N San Mateo Dr"],
                "city": "San Mateo",
                "state": "CA",
                "postal_code": "94401",
                "country": "USA"
            },
            "identity": {
                "given_name": "John",
                "family_name": "Doe",
                "date_of_birth": "1990-01-01",
                "tax_id": "666-55-4321",
                "tax_id_type": "USA_SSN",
                "country_of_citizenship": "USA",
                "country_of_birth": "USA",
                "country_of_tax_residence": "USA",
                "funding_source": ["employment_income"]
            },
            "disclosures": {
                "is_control_person": false,
                "is_affiliated_exchange_or_finra": false,
                "is_politically_exposed": false,
                "immediate_family_exposed": false
            },
            "agreements": [
                {
                    "agreement": "margin_agreement",
                    "signed_at": "2020-09-11T18:09:33Z",
                    "ip_address": "185.13.21.99",
                    "revision": "16.2021.05"
                },
                {
                    "agreement": "account_agreement",
                    "signed_at": "2020-09-11T18:13:44Z",
                    "ip_address": "185.13.21.99",
                    "revision": "16.2021.05"
                },
                {
                    "agreement": "customer_agreement",
                    "signed_at": "2020-09-11T18:13:44Z",
                    "ip_address": "185.13.21.99",
                    "revision": "16.2021.05"
                }
            ],
            "documents": [
                {
                    "document_type": "identity_verification",
                    "document_sub_type": "passport",
                    "content": "/9j/Cg==",
                    "mime_type": "image/jpeg"
                },
                {
                    "document_type": "w8ben",
                    "content": "JVBERi0K",
                    "mime_type": "application/pdf"
                }
            ],
            "trusted_contact": {
                "given_name": "Jane",
                "family_name": "Doe",
                "email_address": "jane.doe@example.com"
            }
        }
        console.log(userData);
        dispatch(createAccount(userData));
    }
    console.log(accData);
    return (
        <div className='detail-section'>
            <h3>Trusted Contacts</h3>
            <div className='form-group'>
                <label className='label'>Given name</label>
                <input
                    type="text"
                    className='form-control'
                    defaultValue={values.given_name}
                    onChange={e => values.setGivenName(e.target.value)}
                    placeholder='Given name'
                />
            </div>
            <div className='form-group'>
                <label className='label'>Family name</label>
                <input
                    type="text"
                    className='form-control'
                    defaultValue={values.family_name}
                    onChange={e => values.setFamilyName(e.target.value)}
                    placeholder='Family name'
                />
            </div>
            <button className='btn' onClick={handleBack}>Back</button>
            <button className='btn' onClick={handleAccount}>Submit</button>
        </div>
    )
}

export default TrustedContacts
